class Trabajadore_model extends CI_Model
{
    function __construct()
    {
        parent::__construct();
    }
    
    /*
     * Get trabajadore by cedula
     */
    function get_trabajadore($cedula)
    {
        return $this->db->get_where('trabajadores',array('cedula'=>$cedula))->row_array();
    }
    
    /*
     * Get all trabajadores count
     */
    function get_all_trabajadores_count()
    {
        $this->db->from('trabajadores');
        return $this->db->count_all_results();
    }
        
    /*
     * Get all trabajadores
     */
    function get_all_trabajadores($params = array())
    {
        $this->db->order_by('cedula', 'desc');
        if(isset($params) && !empty($params))
        {
            $this->db->limit($params['limit'], $params['offset']);
        }
        return $this->db->get('trabajadores')->result_array();
    }
        
    /*
     * function to add new trabajadore
     */
    function add_trabajadore($params)
    {
        $this->db->insert('trabajadores',$params);
        return $this->db->insert_id();
    }
    
    /*
     * function to update trabajadore
     */
    function update_trabajadore($cedula,$params)
    {
        $this->db->where('cedula',$cedula);
        return $this->db->update('trabajadores',$params);
    }
    
    /*
     * function to delete trabajadore
     */
    function delete_trabajadore($cedula)
    {
        return $this->db->delete('trabajadores',array('cedula'=>$cedula));
    }
}
